import React, {useState, useEffect} from 'react';
import axios from 'axios';
import Key from '../Key';


const Current = (props) => {
    let zipCode = props.zipCode;
    const [data, setData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

  useEffect(() => {
    const getData = async () => {
      try {
        const response = await axios.get(
            `http://api.weatherapi.com/v1/current.json?key=${Key}&q=${zipCode}`
        );
        setData(response.data);
        setError(null);
        console.log(data);
      } catch (err) {
        setError(err.message);
        console.log(error);
        setData(null);
      } finally {
        setLoading(false);
      }
    };
    getData();
  }, []);

  console.log(data);

    if (loading) {
        return (
            <h3>Currently Loading Weather Data...</h3>
        )
    } else {
    return (
        <div className='currentBox'>
            <h1 className='city'>{data.location.name}</h1>
            <p><img className='icon' src={data.current.condition.icon} alt="weather_icon" /> </p>

            <p>Temperature: {data.current.temp_f} F</p>
            <p>UV Index: {data.current.uv}</p>
            <p>Humidity: {data.current.humidity}%</p>
            <p>Precipitation: {data.current.precip_in} Inches</p>

        </div>

    );
    }
    };

export default Current;
